table.insert(main_help_table,".zhan 占卜命运的经典版本")
function zhan(msg,msg_time,buddy_name,buddy_num,qun_name,qun_num)
	if string.match(msg,"^%.zhan")==nil then return end
	math.randomseed(os.time()+n)
	math.random()
	local a=math.random(1,64)
	local reason=string.match(msg,"^%.zhan (.*)")
	if reason~=nil then
		say_qun(buddy_name.."在"..msg_time.."对"..reason.."进行了一次占卜:\n"..T[a],qun_num)
	else
		say_qun(buddy_name.."在"..msg_time.."进行了一次占卜:\n"..T[a],qun_num)
	end
end
